// client/src/pages/Register.js
import React, { useState } from 'react';
import { api } from '../api';
import Toast from '../components/Toast';

export default function Register() {
  const [form, setForm] = useState({ name: '', email: '', password: '', inviteCode: '' });
  const [busy, setBusy] = useState(false);
  const [msg, setMsg] = useState(null);

  const onChange = (e) => setForm((f) => ({ ...f, [e.target.name]: e.target.value }));

  const onSubmit = async (e) => {
    e.preventDefault();
    if (busy) return;
    setBusy(true);
    setMsg(null);
    try {
      const payload = {
        name: form.name.trim(),
        email: form.email.trim().toLowerCase(),
        password: form.password
      };
      if (form.inviteCode.trim()) payload.inviteCode = form.inviteCode.trim();
      await api.post('/auth/register', payload);
      setMsg({ type: 'success', text: 'נרשמת בהצלחה! אפשר להתחבר.' });
      setForm({ name: '', email: '', password: '', inviteCode: '' });
    } catch (err) {
      const text = err?.response?.data?.message || 'שגיאה בהרשמה';
      setMsg({ type: 'error', text });
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="container" style={{ maxWidth: 420, marginTop: 40 }}>
      <h2 style={{ textAlign: 'center' }}>הרשמה</h2>
      {msg && <Toast type={msg.type} message={msg.text} onClose={() => setMsg(null)} />}
      <form onSubmit={onSubmit} style={{ display: 'grid', gap: 12 }}>
        <input name="name" placeholder="שם מלא" value={form.name} onChange={onChange} required />
        <input name="email" placeholder="אימייל" type="email" value={form.email} onChange={onChange} required />
        <input name="password" placeholder="סיסמה (6+)" type="password" value={form.password} onChange={onChange} minLength={6} required />
        {/* אופציונלי: רק למקרה של אדמין ראשון עם קוד הזמנה */}
        <input name="inviteCode" placeholder="Invite code (אופציונלי)" value={form.inviteCode} onChange={onChange} />
        <button disabled={busy}>{busy ? 'שולח…' : 'הרשמה'}</button>
      </form>
    </div>
  );
}
