// client/src/App.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import AdminUsers from './pages/AdminUsers';
import Kiosk from './pages/Kiosk';
import QRScan from './pages/QRScan';

function getAuth() {
  // תמיכה גם במבנה הישן (token) וגם בחדש (auth { token, user })
  const token = localStorage.getItem('token');
  if (token) return { token };
  try {
    const auth = JSON.parse(localStorage.getItem('auth') || '{}');
    if (auth?.token) return auth;
  } catch {}
  return { token: null };
}

function getUser() {
  try {
    const auth = JSON.parse(localStorage.getItem('auth') || '{}');
    return auth?.user || null;
  } catch {
    return null;
  }
}

function isAuthed() {
  const { token } = getAuth();
  return !!token;
}

function isAdmin() {
  const u = getUser();
  return u?.role === 'admin' || !!u?.permissions?.usersManage;
}

function canKiosk() {
  const u = getUser();
  return u?.role === 'admin' || !!u?.permissions?.kioskAccess;
}

export default function App() {
  return (
    <Routes>
      <Route path="/login" element={isAuthed() ? <Navigate to="/" replace /> : <Login />} />
      <Route path="/register" element={isAuthed() ? <Navigate to="/" replace /> : <Register />} />

      <Route path="/" element={isAuthed() ? <Dashboard /> : <Navigate to="/login" replace />} />

      <Route
        path="/admin/users"
        element={
          isAuthed()
            ? (isAdmin() ? <AdminUsers /> : <div className="p-4">No permission to access this page</div>)
            : <Navigate to="/login" replace />
        }
      />

      <Route
        path="/kiosk"
        element={
          isAuthed()
            ? (canKiosk() ? <Kiosk /> : <div className="p-4">No permission to access this page</div>)
            : <Navigate to="/login" replace />
        }
      />

      {/* דף סריקה בטלפון – דורש התחברות */}
      <Route path="/qr" element={isAuthed() ? <QRScan /> : <Navigate to="/login" replace />} />

      <Route path="*" element={<Navigate to={isAuthed() ? '/' : '/login'} replace />} />
    </Routes>
  );
}
