// client/src/pages/Login.js
import React, { useState } from 'react';
import { api } from '../api';

export default function Login() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [busy, setBusy] = useState(false);
  const [error, setError] = useState('');

  const onChange = (e) => setForm((f) => ({ ...f, [e.target.name]: e.target.value }));

  const onSubmit = async (e) => {
    e.preventDefault();
    if (busy) return;
    setBusy(true);
    setError('');
    try {
      const data = await api('/api/auth/login', {
        method: 'POST',
        body: JSON.stringify({
          email: form.email.trim().toLowerCase(),
          password: form.password
        })
      });

      // שומרים במבנה החדש
      const auth = { token: data.token, user: data.user };
      localStorage.setItem('auth', JSON.stringify(auth));

      // תאימות לאחור: מנקים מפתחות ישנים כדי שלא יבלבלו
      localStorage.removeItem('token');
      localStorage.removeItem('user');

      // רענון הניווט
      window.location.replace('/');
    } catch (err) {
      setError(err.message || 'Login failed');
    } finally {
      setBusy(false);
    }
  };

  return (
    <div className="container" style={{ maxWidth: 420, marginTop: 60 }}>
      <h2 style={{ textAlign: 'center', marginBottom: 12 }}>התחברות</h2>
      {error && <div style={{ color: '#d00', marginBottom: 10 }}>{error}</div>}
      <form onSubmit={onSubmit} style={{ display: 'grid', gap: 12 }}>
        <input
          type="email"
          name="email"
          placeholder="אימייל"
          value={form.email}
          onChange={onChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="סיסמה"
          value={form.password}
          onChange={onChange}
          required
        />
        <button disabled={busy}>{busy ? 'מתחבר…' : 'התחברות'}</button>
      </form>
    </div>
  );
}
