// client/src/pages/AdminUsers.js
import React, { useEffect, useState } from 'react';
import { api } from '../api';

export default function AdminUsers() {
  const [rows, setRows] = useState([]);
  const [msg, setMsg] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const load = async () => {
      setLoading(true);
      setMsg('');
      try {
        // ✅ הנתיב הנכון לפי השרת
        const data = await api('/api/admin/users');
        setRows(Array.isArray(data) ? data : []);
      } catch (err) {
        setMsg(err.message || 'שגיאה בטעינה');
      } finally {
        setLoading(false);
      }
    };
    load();
  }, []);

  if (msg && /unauthenticated|Missing token|Invalid token/i.test(msg)) {
    return <div style={{ color: '#d00', padding: 20 }}>נדרש להתחבר מחדש</div>;
  }
  if (msg && /forbidden/i.test(msg)) {
    return <div style={{ color: '#d00', padding: 20 }}>אין לך הרשאה לעמוד זה</div>;
  }

  return (
    <div className="container" style={{ padding: 20 }}>
      <h2 style={{ textAlign: 'end' }}>ניהול משתמשים</h2>
      {loading ? (
        <div style={{ padding: 20, textAlign: 'center' }}>טוען…</div>
      ) : (
        <div style={{ overflowX: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th>שם</th>
                <th>אימייל</th>
                <th>תפקיד</th>
                <th>מחלקה</th>
                <th>פעיל</th>
              </tr>
            </thead>
            <tbody>
              {rows.map((u) => (
                <tr key={u._id || u.id}>
                  <td>{u.name}</td>
                  <td>{u.email}</td>
                  <td>{u.role}</td>
                  <td>{u.department || '-'}</td>
                  <td>{u.active ? 'כן' : 'לא'}</td>
                </tr>
              ))}
              {!rows.length && (
                <tr>
                  <td colSpan={5} style={{ textAlign: 'center' }}>אין נתונים</td>
                </tr>
              )}
            </tbody>
          </table>
          {msg && <div style={{ color: '#d00', marginTop: 12 }}>{msg}</div>}
        </div>
      )}
    </div>
  );
}
