        .row { display:flex; align-items:center; }
        .row.end { justify-content:flex-end; }
        .row.wrap { flex-wrap:wrap; }
        .gap { gap:10px; }
        .mb { margin-bottom:10px; }
        .clock { background:#000; color:#fff; border-radius:999px; padding:4px 10px; font-size:12px; }
        .userbox { display:flex; gap:10px; align-items:center; }
        .userbox img { width:32px; height:32px; border-radius:6px; }
        .userbox .name { font-weight:700; font-size:14px; }
        .userbox .email { font-size:12px; color:var(--muted); }
        .chip { background:var(--chip); color:var(--chip-fg); border:1px solid var(--border); border-radius:10px; padding:6px 10px; font-size:13px; }
        .grid { display:grid; grid-template-columns:1fr 1fr; gap:16px; padding:0 16px 20px; }
        @media (max-width: 1024px){ .grid{ grid-template-columns:1fr; } }
        .card { background:var(--card); border:1px solid var(--border); border-radius:16px; box-shadow:0 1px 2px rgba(0,0,0,.03); padding:14px; color:var(--fg); }
        .card * { color: inherit; } /* מבטיח טקסט בהיר במצב כהה גם בתוך רכיבים פנימיים */
        .card-head { display:flex; justify-content:space-between; align-items:center; margin-bottom:8px; }
        .card-head h2 { margin:0; font-size:18px; }
        .muted { color:var(--muted); font-size:13px; }
        .filters { padding:8px 0; border-top:1px solid var(--border); border-bottom:1px solid var(--border); margin-bottom:10px; }
        .input { border:1px solid var(--border); background:transparent; color:var(--fg); border-radius:10px; padding:6px 10px; font-size:14px; }
        .btn { background:#111; color:#fff; border:none; border-radius:10px; padding:8px 14px; font-weight:600; }
        .btn.gray { background:var(--chip); color:var(--chip-fg); border:1px solid var(--border); }
        .btn.tiny { padding:4px 8px; font-size:12px; }
        .btn[disabled]{ opacity:.45; cursor:not-allowed; }
        .link { text-decoration:underline; cursor:pointer; color:var(--fg); opacity:.9; }
        .table-wrap { max-height:480px; overflow:auto; border:1px solid var(--border); border-radius:10px; }
        .table { width:100%; border-collapse:separate; border-spacing:0; direction:rtl; }
        .table thead th, .table tbody td, .table tfoot td { color:var(--fg); }
        .table thead th { position:sticky; top:0; background:var(--chip); color:var(--chip-fg); text-align:right; padding:10px; border-bottom:1px solid var(--border); font-weight:700; }
        .table tbody td { padding:10px; border-bottom:1px solid var(--border); }
        .table tbody tr:nth-child(even){ background:rgba(255,255,255,.03); }
        html.dark .table tbody tr:nth-child(even){ background:rgba(255,255,255,.04); }
        .table tfoot td { padding:10px; background:var(--chip); color:var(--chip-fg); font-weight:700; position:sticky; bottom:0; }
        .skeleton { height:36px; background:linear-gradient(90deg, rgba(0,0,0,.06), rgba(0,0,0,.02), rgba(0,0,0,.06)); animation:s 1.4s infinite; border-radius:8px; }
