// client/src/pages/Dashboard.js
import React, { useEffect, useMemo, useState } from 'react';
import { api } from '../api';
import Toast from '../components/Toast';
import Modal from '../components/Modal';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import * as XLSX from 'xlsx';

/* ========== Utilities ========== */
function fmtMin(n) {
  if (n == null || isNaN(n)) return '0ד';
  const h = Math.floor(n / 60);
  const m = Math.round(n % 60);
  return `${h}ש ${m}ד`;
}
const toLocalDate = (d) => new Date(d).toLocaleDateString();
const toLocalTime = (d) => new Date(d).toLocaleTimeString();

function todayISO() {
  const d = new Date();
  const off = d.getTimezoneOffset();
  const z = new Date(d.getTime() - off * 60000);
  return z.toISOString().slice(0, 10);
}
async function getGeo() {
  if (!('geolocation' in navigator)) return null;
  return new Promise((resolve) => {
    navigator.geolocation.getCurrentPosition(
      (pos) => resolve({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => resolve(null),
      { enableHighAccuracy: false, timeout: 3000, maximumAge: 60000 }
    );
  });
}
function fileDateStamp(d = new Date()) {
  const pad = (n) => String(n).padStart(2, '0');
  return `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())}`;
}
// value for <input type="datetime-local">
function toDatetimeLocalValue(dt) {
  if (!dt) return '';
  const d = new Date(dt);
  const off = d.getTimezoneOffset();
  const z = new Date(d.getTime() - off * 60000);
  return z.toISOString().slice(0, 16);
}

/* ========== Component ========== */
export default function Dashboard() {
  const [me, setMe] = useState(null);

  // Today
  const [today, setToday] = useState(null);
  const [loadingToday, setLoadingToday] = useState(true);
  const [noteDraft, setNoteDraft] = useState('');

  // Report
  const [from, setFrom] = useState(todayISO());
  const [to, setTo] = useState(todayISO());
  const [userId, setUserId] = useState('');
  const [rows, setRows] = useState([]);
  const [loadingReport, setLoadingReport] = useState(false);

  // UI
  const [msg, setMsg] = useState(null);
  const [confirm, setConfirm] = useState(null);

  // Edit modal
  const [editing, setEditing] = useState(null); // the row being edited
  const [editClockIn, setEditClockIn] = useState('');
  const [editClockOut, setEditClockOut] = useState('');

  // Dark mode (עצמאי)
  const [dark, setDark] = useState(() => {
    try { return localStorage.getItem('tw_dark') === '1'; } catch { return false; }
  });
  useEffect(() => {
    const root = document.documentElement;
    if (dark) root.classList.add('dark'); else root.classList.remove('dark');
    try { localStorage.setItem('tw_dark', dark ? '1' : '0'); } catch {}
  }, [dark]);

  /* ===== Data ===== */
  useEffect(() => {
    let stop = false;
    (async () => {
      try { const data = await api('/api/auth/me'); if (!stop) setMe(data); } catch {}
    })();
    return () => { stop = true; };
  }, []);

  async function refreshToday() {
    setLoadingToday(true);
    setNoteDraft(mine?.notes || '');
    try {
      const t = todayISO();
      const res = await api(`/api/attendance/report?from=${t}&to=${t}`);
      const mine = Array.isArray(res) && res.length ? res[0] : null;
      setToday(mine || null);
    } catch (e) {
      setMsg({ type: 'error', title: 'שגיאה', message: e.message });
    } finally {
      setLoadingToday(false);
    }
  }
  async function refreshReport() {
    setLoadingReport(true);
    try {
      const qs = new URLSearchParams({ from, to });
      if (userId) qs.set('userId', userId);
      const data = await api(`/api/attendance/report?${qs.toString()}`);
      setRows(Array.isArray(data) ? data : []);
    } catch (e) {
      setMsg({ type: 'error', title: 'שגיאה', message: e.message });
    } finally {
      setLoadingReport(false);
    }
  }
  useEffect(() => { refreshToday(); }, []);
  useEffect(() => { refreshReport(); }, [from, to, userId]);

  const hasOpenShift = !!(today && today.clockIn && !today.clockOut);
  const onBreak = useMemo(() => {
    if (!today || !today.breaks) return false;
    const last = today.breaks[today.breaks.length - 1];
    return !!(last && last.start && !last.end);
  }, [today]);

  const liveMinutes = useLiveMinutes(today);

  /* ===== Actions ===== */
  async function saveTodayNote() {
  if (!today?._id) return;
  try {
    await api(`/api/attendance/${today._id}/notes`, {
      method: 'PATCH',
      body: JSON.stringify({ notes: noteDraft || '' })
    });
    setMsg({ type: 'success', title: 'הערה נשמרה' });
    await refreshToday();
    await refreshReport?.(); // אם קיימת אצלך
  } catch (e) {
    setMsg({ type: 'error', title: 'שגיאה בשמירת הערה', message: e.message });
  }
}

  async function doClockIn() {
    try {
      const coords = await getGeo();
      const body = coords ? { coords } : undefined;
      await api('/api/attendance/clockin', { method: 'POST', body: JSON.stringify(body) });
      setMsg({ type: 'success', title: 'Clock In בוצע' });
      await refreshToday(); await refreshReport();
    } catch (e) { setMsg({ type: 'error', title: 'שגיאה', message: e.message }); }
  }
  async function doClockOut() {
    if (onBreak) {
      setConfirm({
        title: 'סגירת משמרת בזמן הפסקה',
        body: 'יש הפסקה פתוחה. לסגור אותה ולבצע Clock Out?',
        onConfirm: async () => {
          try { await api('/api/attendance/break/end', { method: 'POST' }); } catch {}
          await reallyClockOut();
        }
      });
      return;
    }
    await reallyClockOut();
  }
  async function reallyClockOut() {
    try {
      const coords = await getGeo();
      const body = coords ? { coords } : undefined;
      await api('/api/attendance/clockout', { method: 'POST', body: JSON.stringify(body) });
      setMsg({ type: 'success', title: 'Clock Out בוצע' });
      await refreshToday(); await refreshReport();
    } catch (e) { setMsg({ type: 'error', title: 'שגיאה', message: e.message }); }
  }
  async function startBreak() {
    try {
      const coords = await getGeo();
      const body = coords ? { coords } : undefined;
      await api('/api/attendance/break/start', { method: 'POST', body: JSON.stringify(body) });
      setMsg({ type: 'success', title: 'הפסקה התחילה' });
      await refreshToday(); await refreshReport();
    } catch (e) { setMsg({ type: 'error', title: 'שגיאה', message: e.message }); }
  }
  async function endBreak() {
    try {
      const coords = await getGeo();
      const body = coords ? { coords } : undefined;
      await api('/api/attendance/break/end', { method: 'POST', body: JSON.stringify(body) });
      setMsg({ type: 'success', title: 'הפסקה הסתיימה' });
      await refreshToday(); await refreshReport();
    } catch (e) { setMsg({ type: 'error', title: 'שגיאה', message: e.message }); }
  }

  const totals = useMemo(() => {
    const sum = (k) => rows.reduce((a, r) => a + (Number(r[k]) || 0), 0);
    return { regularMinutes: sum('regularMinutes'), overtimeMinutes: sum('overtimeMinutes'), totalMinutes: sum('totalMinutes') };
  }, [rows]);

  /* ===== Edit row ===== */
  function openEdit(row) {
    setEditing(row);
    setEditClockIn(toDatetimeLocalValue(row.clockIn));
    setEditClockOut(toDatetimeLocalValue(row.clockOut));
  }
  async function saveEdit() {
    if (!editing?._id) return;
    try {
      const payload = {};
      if (editClockIn) payload.clockIn = new Date(editClockIn).toISOString();
      if (editClockOut) payload.clockOut = new Date(editClockOut).toISOString();

      await api(`/api/attendance/${editing._id}`, {
        method: 'PUT',
        body: JSON.stringify(payload),
      });
      setMsg({ type: 'success', title: 'רשומה עודכנה בהצלחה' });
      setEditing(null);
      await refreshToday(); await refreshReport();
    } catch (e) {
      setMsg({ type: 'error', title: 'עדכון נכשל', message: e.message });
    }
  }

  /* ===== Exports ===== */
  function exportCSV() {
  const header = ['date','clockIn','clockOut','regularMinutes','overtimeMinutes','totalMinutes','notes'];
  const lines = [header.join(',')].concat(
    rows.map(r => [
      r.date,
      r.clockIn ? new Date(r.clockIn).toLocaleString() : '',
      r.clockOut ? new Date(r.clockOut).toLocaleString() : '',
      r.regularMinutes ?? 0,
      r.overtimeMinutes ?? 0,
      r.totalMinutes ?? 0,
      (r.notes || '').replace(/\n/g, ' ')
    ].join(','))
  );
  const blob = new Blob([lines.join('\n')], { type: 'text/csv;charset=utf-8' });
  const a = document.createElement('a');
  a.href = URL.createObjectURL(blob);
  a.download = `Costoro-${(userId || 'me')}-${from}_to_${to}.csv`;
  a.click();
}
  function exportExcel() {
  const rows2 = rows.map(r => ({
    Date: r.date,
    ClockIn: r.clockIn ? new Date(r.clockIn).toLocaleString() : '',
    ClockOut: r.clockOut ? new Date(r.clockOut).toLocaleString() : '',
    RegularMinutes: r.regularMinutes || 0,
    OvertimeMinutes: r.overtimeMinutes || 0,
    TotalMinutes: r.totalMinutes || 0,
    Notes: r.notes || ''
  }));
  const wb = XLSX.utils.book_new();
  const ws = XLSX.utils.json_to_sheet(rows2);
  XLSX.utils.book_append_sheet(wb, ws, 'Report');
  XLSX.writeFile(wb, `Costoro-${(userId || 'me')}-${from}_to_${to}.xlsx`);
}
  async function exportPDF() {
    const doc = new jsPDF('p', 'pt');
    const marginX = 36;
    const lineY = 64;

    doc.setFontSize(16);
    doc.text('Costoro - Attendance Report', marginX, 30);
    doc.setFontSize(11);
    const who = userId ? `User: ${userId}` : (me?.name ? `User: ${me.name}` : 'User: Me');
    doc.text(`${who}    Range: ${from} → ${to}`, marginX, 46);
    doc.setLineWidth(0.5);
    doc.line(marginX, lineY, 559, lineY);

    try {
      const img = await loadImage('/logo.png');
      const cnv = document.createElement('canvas');
      cnv.width = img.width; cnv.height = img.height;
      const ctx = cnv.getContext('2d');
      ctx.drawImage(img, 0, 0);
      const base64 = cnv.toDataURL('image/png');
      doc.addImage(base64, 'PNG', 500, 16, 56, 56);
    } catch {}

    const body = rows.map(r => [
      r.date,
      r.clockIn ? new Date(r.clockIn).toLocaleString() : '-',
      r.clockOut ? new Date(r.clockOut).toLocaleString() : '-',
      fmtMin(r.regularMinutes || 0),
      fmtMin(r.overtimeMinutes || 0),
      fmtMin(r.totalMinutes || 0),
    ]);
    doc.autoTable({
  startY: lineY + 16,
  head: [['תאריך','Clock In','Clock Out','רגיל','נוספות','ס״כ','הערות']],
  body: rows.map(r => [
    r.date,
    r.clockIn ? new Date(r.clockIn).toLocaleString() : '-',
    r.clockOut ? new Date(r.clockOut).toLocaleString() : '-',
    fmtMin(r.regularMinutes || 0),
    fmtMin(r.overtimeMinutes || 0),
    fmtMin(r.totalMinutes || 0),
    r.notes || ''
  ]),
  styles: { font: 'helvetica', fontSize: 10 },
  headStyles: { fillColor: [20, 20, 20], textColor: [255, 255, 255] }
});
    const finalY = doc.lastAutoTable.finalY || (lineY + 16);
    doc.setFontSize(11);
    doc.text(`Totals: רגיל ${fmtMin(totals.regularMinutes)}, נוספות ${fmtMin(totals.overtimeMinutes)}, סה״כ ${fmtMin(totals.totalMinutes)}`, marginX, finalY + 24);
    doc.save(`Costoro-${(me?.name || 'me')}-${fileDateStamp()}.pdf`);
  }

  /* ===== Presets ===== */
  function setPreset(kind) {
    const d = new Date();
    const toLocalISO = (x) => {
      const off = x.getTimezoneOffset();
      const z = new Date(x.getTime() - off * 60000);
      return z.toISOString().slice(0, 10);
    };
    if (kind === 'today') {
      const t = toLocalISO(new Date()); setFrom(t); setTo(t);
    } else if (kind === 'yesterday') {
      const y = new Date(d); y.setDate(d.getDate() - 1);
      const t = toLocalISO(y); setFrom(t); setTo(t);
    } else if (kind === 'week') {
      const day = d.getDay();
      const monday = new Date(d); const sunday = new Date(d);
      monday.setDate(d.getDate() - ((day + 6) % 7));
      sunday.setDate(monday.getDate() + 6);
      setFrom(toLocalISO(monday)); setTo(toLocalISO(sunday));
    } else if (kind === 'month') {
      const first = new Date(d.getFullYear(), d.getMonth(), 1);
      const last = new Date(d.getFullYear(), d.getMonth() + 1, 0);
      setFrom(toLocalISO(first)); setTo(toLocalISO(last));
    }
  }

  function logout() {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('auth');
    window.location.href = '/login';
  }

  /* ===== Render ===== */
  const canEditRows = !!(me?.permissions?.attendanceEdit);

  return (
    <div className="page">
      <Toast {...(msg || {})} onClose={() => setMsg(null)} show={!!msg} />
      {confirm && (
        <Modal title={confirm.title} onClose={() => setConfirm(null)}>
          <div className="space-y">
            <p>{confirm.body}</p>
            <div className="row end gap">
              <button className="btn" onClick={() => { setConfirm(null); confirm.onConfirm?.(); }}>אישור</button>
              <button className="btn gray" onClick={() => setConfirm(null)}>ביטול</button>
            </div>
          </div>
        </Modal>
      )}

      {/* Edit modal */}
      {editing && (
        <Modal title={`עריכת יום ${toLocalDate(editing.date || editing.clockIn || Date.now())}`} onClose={() => setEditing(null)}>
          <div className="space-y">
            <label className="stack">
              <span>Clock In</span>
              <input className="input" type="datetime-local" value={editClockIn} onChange={e => setEditClockIn(e.target.value)} />
            </label>
            <label className="stack">
              <span>Clock Out</span>
              <input className="input" type="datetime-local" value={editClockOut} onChange={e => setEditClockOut(e.target.value)} />
            </label>
            <div className="row end gap">
              <button className="btn" onClick={saveEdit}>שמור</button>
              <button className="btn gray" onClick={() => setEditing(null)}>בטל</button>
            </div>
          </div>
        </Modal>
      )}

      {/* Top bar */}
      <div className="topbar">
        <div className="row gap">
          <button className="chip" onClick={() => setDark(v => !v)}>{dark ? 'מצב בהיר' : 'מצב כהה'}</button>
          <button className="chip" onClick={() => window.open('/kiosk', '_blank')}>עמדת סריקה (QR)</button>
          {me?.role === 'admin' && (
            <button className="chip" onClick={() => (window.location.href = '/admin/users')}>ניהול משתמשים</button>
          )}
        </div>
        <div className="row gap">
          <div className="clock">{new Date().toLocaleTimeString()}</div>
          <div className="userbox">
            <img src="/logo.png" alt="Logo" />
            <div>
              <div className="name">שלום {me?.name || '...'}</div>
              <div className="email">{me?.email || ''}</div>
            </div>
          </div>
        </div>
      </div>

      {/* Grid */}
      <div className="grid">
        {/* Report (left) */}
        <section className="card">
          <div className="card-head">
            <h2>דוח לפי תאריכים</h2>
            <div className="row gap">
              <a className="link" onClick={exportCSV}>CSV</a>
              <a className="link" onClick={exportExcel}>Excel</a>
              <a className="link" onClick={exportPDF}>PDF</a>
            </div>
          </div>

          <div className="filters">
            <div className="row gap wrap">
              <select className="input" value={userId} onChange={(e) => setUserId(e.target.value)}>
                <option value="">— כול/ה את עצמי —</option>
              </select>
              <input className="input" type="date" value={from} onChange={(e) => setFrom(e.target.value)} />
              <span>עד</span>
              <input className="input" type="date" value={to} onChange={(e) => setTo(e.target.value)} />
              <div className="row gap">
                <button className="chip" onClick={() => setPreset('today')}>היום</button>
                <button className="chip" onClick={() => setPreset('yesterday')}>אתמול</button>
                <button className="chip" onClick={() => setPreset('week')}>השבוע</button>
                <button className="chip" onClick={() => setPreset('month')}>החודש</button>
              </div>
            </div>
          </div>

          <div className="table-wrap">
            <table className="table">
              <thead>
                <tr>
                  <th>תאריך</th>
                  <th>Clock In</th>
                  <th>Clock Out</th>
                  <th>רגיל</th>
                  <th>נוספות</th>
                  <th>ס״כ</th>
                  <th>הערות</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {loadingReport ? (
                  Array.from({ length: 6 }).map((_, i) => (
                    <tr key={i}><td colSpan="7"><div className="skeleton"/></td></tr>
                  ))
                ) : (
                  rows.map((r) => (
                    <tr key={r._id || r.date}>
                      <td className="ltr">{r.date}</td>
                      <td className="ltr">{r.clockIn ? toLocalTime(r.clockIn) : '-'}</td>
                      <td className="ltr">{r.clockOut ? toLocalTime(r.clockOut) : '-'}</td>
                      <td className="ltr">{fmtMin(r.regularMinutes || 0)}</td>
                      <td className="ltr">{fmtMin(r.overtimeMinutes || 0)}</td>
                      <td className="ltr">{fmtMin(r.totalMinutes || 0)}</td>
                      <td className="ltr">{r.notes || ''}</td>
                      <td>
                        <button
                          className="btn tiny"
                          disabled={!canEditRows || !r._id}
                          onClick={() => openEdit(r)}
                          title={!canEditRows ? 'אין הרשאה לעריכה' : 'ערוך'}
                        >
                          ערוך
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
              {!loadingReport && (
                <tfoot>
                  <tr>
                    <td>סיכום</td>
                    <td></td><td></td>
                    <td className="ltr">{fmtMin(totals.regularMinutes)}</td>
                    <td className="ltr">{fmtMin(totals.overtimeMinutes)}</td>
                    <td className="ltr">{fmtMin(totals.totalMinutes)}</td>
                    <td></td>
                  </tr>
                </tfoot>
              )}
            </table>
          </div>
        </section>

        {/* Today (right) */}
        <section className="card">
          <div className="card-head">
            <h2>היום</h2>
            <div className="muted">{today ? today.date : todayISO()}</div>
          </div>

          <div className="row gap wrap end mb">
            <button className="btn" disabled={hasOpenShift} onClick={doClockIn}>Clock In</button>
            <button className="btn gray" disabled={!hasOpenShift || onBreak} onClick={startBreak}>Start Break</button>
            <button className="btn gray" disabled={!onBreak} onClick={endBreak}>End Break</button>
            <button className="btn gray" disabled={!hasOpenShift} onClick={doClockOut}>Clock Out</button>
          </div>

          {loadingToday ? (
            <div className="skeleton big" />
          ) : !today ? (
            <div className="muted">לא במשמרת</div>
          ) : (
            <div className="two">
              <div className="stack">
                <div><b>Clock In:</b> <span className="ltr">{today.clockIn ? toLocalTime(today.clockIn) : '-'}</span></div>
                <div><b>Clock Out:</b> <span className="ltr">{today.clockOut ? toLocalTime(today.clockOut) : '-'}</span></div>
                <div><b>סה״כ (חי):</b> <span className="ltr">{fmtMin(liveMinutes)}</span></div>
                <div><b>רגיל:</b> <span className="ltr">{fmtMin(today.regularMinutes || 0)}</span></div>
                <div><b>נוספות:</b> <span className="ltr">{fmtMin(today.overtimeMinutes || 0)}</span></div>
              </div>
              <div className="stack">
                <div className="b">הפסקות:</div>
                <ul className="bullets">
                  {(today.breaks || []).map((b, i) => (
                    <li key={i} className="ltr">
                      {b.start ? toLocalTime(b.start) : '-'} → {b.end ? toLocalTime(b.end) : '—'}
                    </li>
                  ))}
                  {!today.breaks?.length && <li>—</li>}
                </ul>
                <div className="stack" style={{marginTop: '8px'}}>
  <label className="b">הערות:</label>
  <textarea
    className="input"
    rows={3}
    value={noteDraft}
    onChange={e => setNoteDraft(e.target.value)}
    placeholder="הוסף הערה למשמרת..."
  />
  <div className="row end" style={{marginTop: '6px'}}>
    <button className="btn" disabled={!today?._id} onClick={saveTodayNote}>שמור הערה</button>
  </div>
</div>

              </div>
            </div>
          )}
        </section>
      </div>

      <div className="footer">
        <button className="btn gray" onClick={logout}>התנתק</button>
        <div className="muted">© Costoro {new Date().getFullYear()}</div>
      </div>

      {/* ===== Local Styles (RTL + Dark) ===== */}
      <style>{`
        :root { --bg:#f4f7fb; --fg:#0f172a; --muted:#6b7280; --card:#ffffff; --border:#e5e7eb; --chip:#eef2f7; --chip-fg:#0f172a; --accent:#111; }
        html.dark { --bg:#0f172a; --fg:#e5e7eb; --muted:#93a1b5; --card:#111827; --border:#273043; --chip:#1f2937; --chip-fg:#e5e7eb; --accent:#f8fafc; }
        html, body, #root { height:100%; }
        body { background:var(--bg); color:var(--fg); }
        .page { min-height:100vh; display:flex; flex-direction:column; gap:16px; }
        .topbar { display:flex; justify-content:space-between; align-items:center; padding:14px 16px; }
        .row { display:flex; align-items:center; }
        .row.end { justify-content:flex-end; }
        .row.wrap { flex-wrap:wrap; }
        .gap { gap:10px; }
        .mb { margin-bottom:10px; }
        .clock { background:#000; color:#fff; border-radius:999px; padding:4px 10px; font-size:12px; }
        .userbox { display:flex; gap:10px; align-items:center; }
        .userbox img { width:32px; height:32px; border-radius:6px; }
        .userbox .name { font-weight:700; font-size:14px; }
        .userbox .email { font-size:12px; color:var(--muted); }
        .chip { background:var(--chip); color:var(--chip-fg); border:1px solid var(--border); border-radius:10px; padding:6px 10px; font-size:13px; }
        .grid { display:grid; grid-template-columns:1fr 1fr; gap:16px; padding:0 16px 20px; }
        @media (max-width: 1024px){ .grid{ grid-template-columns:1fr; } }
        .card { background:var(--card); border:1px solid var(--border); border-radius:16px; box-shadow:0 1px 2px rgba(0,0,0,.03); padding:14px; color:var(--fg); }
        .card * { color: inherit; } /* מבטיח טקסט בהיר במצב כהה גם בתוך רכיבים פנימיים */
        .card-head { display:flex; justify-content:space-between; align-items:center; margin-bottom:8px; }
        .card-head h2 { margin:0; font-size:18px; }
        .muted { color:var(--muted); font-size:13px; }
        .filters { padding:8px 0; border-top:1px solid var(--border); border-bottom:1px solid var(--border); margin-bottom:10px; }
        .input { border:1px solid var(--border); background:transparent; color:var(--fg); border-radius:10px; padding:6px 10px; font-size:14px; }
        .btn { background:#111; color:#fff; border:none; border-radius:10px; padding:8px 14px; font-weight:600; }
        .btn.gray { background:var(--chip); color:var(--chip-fg); border:1px solid var(--border); }
        .btn.tiny { padding:4px 8px; font-size:12px; }
        .btn[disabled]{ opacity:.45; cursor:not-allowed; }
        .link { text-decoration:underline; cursor:pointer; color:var(--fg); opacity:.9; }
        .table-wrap { max-height:480px; overflow:auto; border:1px solid var(--border); border-radius:10px; }
