    </div>
  );
}

/* ===== Hooks ===== */
function useLiveMinutes(today) {
  const [min, setMin] = useState(0);
  useEffect(() => {
    function calc() {
      if (!today || !today.clockIn) return setMin(0);
      const start = new Date(today.clockIn).getTime();
      let now = Date.now();
      let workingMs = now - start;
      const breaks = Array.isArray(today.breaks) ? today.breaks : [];
      for (const b of breaks) {
        const s = b.start ? new Date(b.start).getTime() : null;
        const e = b.end ? new Date(b.end).getTime() : null;
        if (s) {
          if (e) workingMs -= Math.max(0, e - s);
          else workingMs -= Math.max(0, now - s);
        }
      }
      setMin(Math.max(0, Math.floor(workingMs / 60000)));
    }
    calc();
    const t = setInterval(calc, 1000);
    return () => clearInterval(t);
  }, [today?.clockIn, JSON.stringify(today?.breaks || [])]);
  return min;
}

function loadImage(src) {
  return new Promise((resolve, reject) => {
    const img = new Image();
    img.onload = () => resolve(img);
    img.onerror = reject;
    img.src = src;
  });
}
