// client/src/pages/AdminUsers.js
import React, { useEffect, useMemo, useState } from 'react';
import { api } from '../api';
import Modal from '../components/Modal';
import Toast from '../components/Toast';

const emptyPerms = { manageUsers:false, attendanceReadAll:false, attendanceEdit:false, exportReports:true };

export default function AdminUsers() {
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);
  const [msg, setMsg] = useState(null);

  // Edit
  const [editOpen, setEditOpen] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [form, setForm] = useState({
    name: '', email: '', role: 'user', department: '', active: true, permissions: { ...emptyPerms }
  });

  // Reset password
  const [resetOpen, setResetOpen] = useState(false);
  const [resetId, setResetId] = useState(null);
  const [resetPass, setResetPass] = useState('');

  // Delete
  const [delOpen, setDelOpen] = useState(false);
  const [delUser, setDelUser] = useState(null);

  const showToast = (type, title, message='') => setMsg({ show:true, type, title, message });
  const closeToast = () => setMsg((m)=>m ? { ...m, show:false } : m);

  const load = async () => {
    setLoading(true);
    try {
      const data = await api('/api/admin/users');
      setRows(Array.isArray(data) ? data : []);
    } catch (err) {
      showToast('error', 'שגיאה', err.message || 'שגיאה בטעינת משתמשים');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); }, []);

  const openEdit = (u) => {
    setEditUser(u);
    setForm({
      name: u.name || '',
      email: u.email || '',
      role: u.role || 'user',
      department: u.department || '',
      active: !!u.active,
      permissions: { ...emptyPerms, ...(u.permissions || {}) }
    });
    setEditOpen(true);
  };

  const saveEdit = async () => {
    try {
      const payload = {
        role: form.role,
        department: form.department,
        active: form.active,
        permissions: form.permissions
      };
      const updated = await api(`/api/admin/users/${editUser._id || editUser.id}`, {
        method: 'PATCH',
        body: JSON.stringify(payload)
      });
      setRows((list) => list.map((x) => (x._id === updated._id || x.id === updated.id ? updated : x)));
      setEditOpen(false);
      showToast('success', 'נשמר', 'המשתמש עודכן בהצלחה');
    } catch (err) {
      showToast('error', 'שגיאה', err.message || 'עדכון נכשל');
    }
  };

  const openReset = (u) => { setResetId(u._id || u.id); setResetPass(''); setResetOpen(true); };

  const doReset = async () => {
    try {
      await api(`/api/admin/users/${resetId}`, {
        method: 'PATCH',
        body: JSON.stringify({ password: resetPass })
      });
      setResetOpen(false);
      showToast('success', 'הסיסמה אופסה', 'המשתמש יעודכן בהתחברות הבאה');
    } catch (err) {
      showToast('error', 'שגיאה', err.message || 'איפוס סיסמה נכשל');
    }
  };

  const openDelete = (u) => { setDelUser(u); setDelOpen(true); };

  const doDelete = async () => {
    try {
      await api(`/api/admin/users/${delUser._id || delUser.id}`, { method: 'DELETE' });
      setRows((list) => list.filter((x) => (x._id||x.id) !== (delUser._id||delUser.id)));
      setDelOpen(false);
      showToast('success', 'נמחק', 'המשתמש נמחק בהצלחה');
    } catch (err) {
      showToast('error', 'שגיאה', err.message || 'מחיקה נכשלה');
    }
  };

  const totalAdmins = useMemo(() => rows.filter(r => r.role === 'admin').length, [rows]);

    return (
    <div className="min-h-screen p-4">
      <Toast show={!!msg?.show} type={msg?.type} title={msg?.title} message={msg?.message} onClose={closeToast} />

      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-semibold">ניהול משתמשים</h2>
        <div className="flex gap-2">
          <a
            href="/"
            className="px-3 py-1.5 rounded-xl border bg-white hover:bg-slate-50"
          >
            חזרה לעמוד הראשי
          </a>
          <button
            onClick={load}
            className="px-3 py-1.5 rounded-xl border bg-white hover:bg-slate-50"
          >
            רענון
          </button>
        </div>
      </div>


      <div className="bg-white rounded-2xl shadow overflow-x-auto">
        <table className="min-w-full text-sm">
          <thead>
            <tr className="border-b bg-slate-50">
              <th className="px-4 py-3 text-right">שם</th>
              <th className="px-4 py-3 text-right">אימייל</th>
              <th className="px-4 py-3 text-right">תפקיד</th>
              <th className="px-4 py-3 text-right">מחלקה</th>
              <th className="px-4 py-3 text-right">פעיל</th>
              <th className="px-4 py-3 text-right">פעולות</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td className="px-4 py-5 text-center text-slate-500" colSpan={6}>טוען…</td></tr>
            ) : rows.length ? rows.map((u) => (
              <tr key={u._id || u.id} className="border-b">
                <td className="px-4 py-2">{u.name || '-'}</td>
                <td className="px-4 py-2">{u.email}</td>
                <td className="px-4 py-2">{u.role}</td>
                <td className="px-4 py-2">{u.department || '-'}</td>
                <td className="px-4 py-2">{u.active ? 'כן' : 'לא'}</td>
                <td className="px-4 py-2">
                  <div className="flex gap-2 justify-end">
                    <button className="px-2 py-1 rounded-lg border" onClick={() => openEdit(u)}>ערוך</button>
                    <button className="px-2 py-1 rounded-lg border" onClick={() => openReset(u)}>איפוס סיסמה</button>
                    <button
                      className="px-2 py-1 rounded-lg border text-rose-600"
                      onClick={() => openDelete(u)}
                      disabled={u.role === 'admin' && totalAdmins <= 1}
                      title={(u.role === 'admin' && totalAdmins <= 1) ? 'לא ניתן למחוק את האדמין האחרון' : ''}
                    >
                      מחק
                    </button>
                  </div>
                </td>
              </tr>
            )) : (
              <tr><td className="px-4 py-5 text-center text-slate-500" colSpan={6}>אין משתמשים</td></tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Edit modal */}
      <Modal
        open={editOpen}
        title="עריכת משתמש"
        onClose={() => setEditOpen(false)}
        actions={(
          <>
            <button className="px-4 py-2 rounded-xl border bg-white" onClick={() => setEditOpen(false)}>ביטול</button>
            <button className="px-4 py-2 rounded-xl bg-black text-white" onClick={saveEdit}>שמירה</button>
          </>
        )}
      >
        {editUser && (
          <div className="grid gap-3">
            <div className="grid grid-cols-2 gap-3">
              <div>
                <label className="block text-sm mb-1">שם</label>
                <input className="border rounded-xl px-3 py-2 w-full" value={form.name} disabled />
              </div>
              <div>
                <label className="block text-sm mb-1">אימייל</label>
                <input className="border rounded-xl px-3 py-2 w-full" value={form.email} disabled dir="ltr" />
              </div>
            </div>

            <div className="grid grid-cols-3 gap-3">
              <div>
                <label className="block text-sm mb-1">תפקיד</label>
                <select
                  className="border rounded-xl px-3 py-2 w-full"
                  value={form.role}
                  onChange={(e)=>setForm(f=>({ ...f, role:e.target.value }))}
                >
                  <option value="user">user</option>
                  <option value="admin">admin</option>
                </select>
              </div>
              <div>
                <label className="block text-sm mb-1">מחלקה</label>
                <input
                  className="border rounded-xl px-3 py-2 w-full"
                  value={form.department}
                  onChange={(e)=>setForm(f=>({ ...f, department:e.target.value }))}
                />
              </div>
              <div className="flex items-end">
                <label className="flex items-center gap-2 text-sm">
                  <input
                    type="checkbox"
                    checked={form.active}
                    onChange={(e)=>setForm(f=>({ ...f, active:e.target.checked }))}
                  />
                  פעיל
                </label>
              </div>
            </div>

            <div className="mt-2 p-3 rounded-xl border bg-slate-50">
              <div className="font-medium mb-2">הרשאות</div>
              <div className="grid md:grid-cols-2 gap-2 text-sm">
                <label className="flex items-center gap-2">
                  <input type="checkbox"
                    checked={!!form.permissions.manageUsers}
                    onChange={(e)=>setForm(f=>({ ...f, permissions:{ ...f.permissions, manageUsers: e.target.checked } }))}
                  />
                  ניהול משתמשים
                </label>
                <label className="flex items-center gap-2">
                  <input type="checkbox"
                    checked={!!form.permissions.attendanceReadAll}
                    onChange={(e)=>setForm(f=>({ ...f, permissions:{ ...f.permissions, attendanceReadAll: e.target.checked } }))}
                  />
                  צפייה בדוחות של כולם
                </label>
                <label className="flex items-center gap-2">
                  <input type="checkbox"
                    checked={!!form.permissions.attendanceEdit}
                    onChange={(e)=>setForm(f=>({ ...f, permissions:{ ...f.permissions, attendanceEdit: e.target.checked } }))}
                  />
                  עריכת רשומות נוכחות
                </label>
                <label className="flex items-center gap-2">
                  <input type="checkbox"
                    checked={!!form.permissions.exportReports}
                    onChange={(e)=>setForm(f=>({ ...f, permissions:{ ...f.permissions, exportReports: e.target.checked } }))}
                  />
                  ייצוא דוחות (CSV/XLSX/PDF)
                </label>
              </div>
            </div>
          </div>
        )}
      </Modal>

      {/* Reset password modal */}
      <Modal
        open={resetOpen}
        title="איפוס סיסמה"
        onClose={()=>setResetOpen(false)}
        actions={(
          <>
            <button className="px-4 py-2 rounded-xl border bg-white" onClick={()=>setResetOpen(false)}>ביטול</button>
            <button
              className="px-4 py-2 rounded-xl bg-black text-white"
              onClick={doReset}
              disabled={!resetPass || resetPass.length < 6}
            >
              אפס סיסמה
            </button>
          </>
        )}
      >
        <div className="grid gap-2">
          <label className="block text-sm mb-1">סיסמה חדשה (6+ תווים)</label>
          <input
            type="password"
            className="border rounded-xl px-3 py-2 w-full"
            value={resetPass}
            onChange={(e)=>setResetPass(e.target.value)}
            placeholder="••••••••"
            minLength={6}
          />
          <div className="text-xs text-slate-500">האיפוס נכנס לתוקף מיד. המשתמש יוכל להתחבר עם הסיסמה החדשה.</div>
        </div>
      </Modal>

      {/* Delete confirm */}
      <Modal
        open={delOpen}
        title="מחיקת משתמש"
        onClose={()=>setDelOpen(false)}
        actions={(
          <>
            <button className="px-4 py-2 rounded-xl border bg-white" onClick={()=>setDelOpen(false)}>ביטול</button>
            <button className="px-4 py-2 rounded-xl bg-rose-600 text-white" onClick={doDelete}>מחק</button>
          </>
        )}
      >
        <div className="text-sm">
          האם למחוק את <b>{delUser?.name || delUser?.email}</b>?<br/>
          <span className="text-slate-500">הפעולה בלתי הפיכה.</span>
        </div>
      </Modal>
    </div>
  );
}
