import React, { useEffect, useMemo, useState } from 'react';
import { api } from '../api';
import Toast from '../components/Toast';
import Modal from '../components/Modal';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import * as XLSX from 'xlsx';

/* ========== Helpers ========== */
const toLocalDate = (d) => d ? new Date(d).toLocaleDateString() : '';
const toLocalTime = (d) => d ? new Date(d).toLocaleTimeString() : '-';
const pad2 = (n) => String(n).padStart(2, '0');
const todayISO = () => {
  const d = new Date(); const off = d.getTimezoneOffset();
  return new Date(d.getTime() - off * 60000).toISOString().slice(0, 10);
};
function fmtMin(n) {
  const v = Number(n) || 0;
  const h = Math.floor(v / 60), m = Math.round(v % 60);
  return `${h}h ${m}m`;
}
function toDatetimeLocalValue(dt) {
  if (!dt) return '';
  const d = new Date(dt); const off = d.getTimezoneOffset();
  return new Date(d.getTime() - off * 60000).toISOString().slice(0, 16);
}
async function getGeo() {
  if (!navigator.geolocation) return null;
  return new Promise((resolve) => {
    navigator.geolocation.getCurrentPosition(
      (pos) => resolve({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => resolve(null),
      { timeout: 3000, maximumAge: 60000 }
    );
  });
}

/* ========== Component ========== */
export default function Dashboard() {
  // Auth/user
  const [me, setMe] = useState(null);

  // Today
  const [today, setToday] = useState(null);
  const [loadingToday, setLoadingToday] = useState(true);
  const [noteDraft, setNoteDraft] = useState('');

  // Report
  const [from, setFrom] = useState(todayISO());
  const [to, setTo] = useState(todayISO());
  const [userId, setUserId] = useState('');
  const [rows, setRows] = useState([]);
  const [loadingReport, setLoadingReport] = useState(false);

  // UI
  const [msg, setMsg] = useState(null);
  const [editing, setEditing] = useState(null);
  const [editClockIn, setEditClockIn] = useState('');
  const [editClockOut, setEditClockOut] = useState('');
  const [editNotes, setEditNotes] = useState('');

  // Dark mode (kept)
  const [dark, setDark] = useState(() => {
    try { return localStorage.getItem('tw_dark') === '1'; } catch { return false; }
  });
  useEffect(() => {
    const root = document.documentElement;
    if (dark) root.classList.add('dark'); else root.classList.remove('dark');
    try { localStorage.setItem('tw_dark', dark ? '1' : '0'); } catch {}
  }, [dark]);

  /* ===== Loaders ===== */
  useEffect(() => {
    (async () => { try { setMe(await api('/api/auth/me')); } catch {} })();
  }, []);

  async function refreshToday() {
    setLoadingToday(true);
    try {
      const t = todayISO();
      const res = await api(`/api/attendance/report?from=${t}&to=${t}`);
      const mine = Array.isArray(res) && res.length ? res[0] : null;
      setToday(mine || null);
      setNoteDraft(mine?.notes || '');
    } catch (e) {
      setMsg({ type: 'error', title: 'Error', message: e.message });
    } finally { setLoadingToday(false); }
  }
  async function refreshReport() {
    setLoadingReport(true);
    try {
      const qs = new URLSearchParams({ from, to });
      if (userId) qs.set('userId', userId);
      const data = await api(`/api/attendance/report?${qs.toString()}`);
      setRows(Array.isArray(data) ? data : []);
    } catch (e) {
      setMsg({ type: 'error', title: 'Error', message: e.message });
    } finally { setLoadingReport(false); }
  }
  useEffect(() => { refreshToday(); }, []);
  useEffect(() => { refreshReport(); }, [from, to, userId]);

  const shiftOpen = !!(today?.clockIn && !today?.clockOut);
  const onBreak = useMemo(() => {
    const b = today?.breaks || [];
    const last = b[b.length - 1];
    return !!(last?.start && !last?.end);
  }, [today?.breaks, today?.clockIn, today?.clockOut]);

  const liveMinutes = useLiveMinutes(today);

  /* ===== Actions ===== */
  async function doClockIn() {
    try {
      const coords = await getGeo();
      await api('/api/attendance/clockin', { method: 'POST', body: JSON.stringify(coords ? { coords } : {}) });
      setMsg({ type: 'success', title: 'Clock In done' });
      await refreshToday(); await refreshReport();
    } catch (e) { setMsg({ type: 'error', title: 'Error', message: e.message }); }
  }
  async function doClockOut() {
    try {
      if (onBreak) { await api('/api/attendance/break/end', { method: 'POST' }); }
      const coords = await getGeo();
      await api('/api/attendance/clockout', { method: 'POST', body: JSON.stringify(coords ? { coords } : {}) });
      setMsg({ type: 'success', title: 'Clock Out done' });
      await refreshToday(); await refreshReport();
    } catch (e) { setMsg({ type: 'error', title: 'Error', message: e.message }); }
  }
  async function startBreak() {
    try {
      await api('/api/attendance/break/start', { method: 'POST' });
      await refreshToday();
    } catch (e) { setMsg({ type: 'error', title: 'Error', message: e.message }); }
  }
  async function endBreak() {
    try {
      await api('/api/attendance/break/end', { method: 'POST' });
      await refreshToday();
    } catch (e) { setMsg({ type: 'error', title: 'Error', message: e.message }); }
  }
  async function saveTodayNote() {
    try {
      if (!today?._id) return;
      await api(`/api/attendance/${today._id}/notes`, { method: 'PATCH', body: JSON.stringify({ notes: noteDraft || '' }) });
      setMsg({ type: 'success', title: 'Note saved' });
      await refreshToday(); await refreshReport();
    } catch (e) { setMsg({ type: 'error', title: 'Error saving note', message: e.message }); }
  }

  // EDIT (always enabled; if you want permission gate later we’ll add)
  function openEdit(row) {
    setEditing(row);
    setEditClockIn(toDatetimeLocalValue(row.clockIn));
    setEditClockOut(toDatetimeLocalValue(row.clockOut));
    setEditNotes(row.notes || '');
  }
  async function saveEdit() {
    if (!editing?._id) return;
    try {
      const payload = {};
      if (editClockIn) payload.clockIn = new Date(editClockIn).toISOString();
      if (editClockOut) payload.clockOut = new Date(editClockOut).toISOString();
      payload.notes = editNotes ?? '';
      await api(`/api/attendance/${editing._id}`, { method: 'PUT', body: JSON.stringify(payload) });
      setMsg({ type: 'success', title: 'Record updated' });
      setEditing(null);
      await refreshToday(); await refreshReport();
    } catch (e) { setMsg({ type: 'error', title: 'Update failed', message: e.message }); }
  }

  // Exports
  function exportCSV() {
    const header = ['date','clockIn','clockOut','regularMinutes','overtimeMinutes','totalMinutes','notes'];
    const lines = [header.join(',')].concat(
      rows.map(r => [
        r.date,
        r.clockIn ? new Date(r.clockIn).toLocaleString() : '',
        r.clockOut ? new Date(r.clockOut).toLocaleString() : '',
        r.regularMinutes ?? 0,
        r.overtimeMinutes ?? 0,
        r.totalMinutes ?? 0,
        (r.notes || '').replace(/\n/g, ' ')
      ].join(','))
    );
    const blob = new Blob([lines.join('\n')], { type: 'text/csv;charset=utf-8' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = `Report-${(userId || 'me')}-${from}_to_${to}.csv`;
    a.click();
  }
  function exportExcel() {
    const rows2 = rows.map(r => ({
      Date: r.date,
      ClockIn: r.clockIn ? new Date(r.clockIn).toLocaleString() : '',
      ClockOut: r.clockOut ? new Date(r.clockOut).toLocaleString() : '',
      RegularMinutes: r.regularMinutes || 0,
      OvertimeMinutes: r.overtimeMinutes || 0,
      TotalMinutes: r.totalMinutes || 0,
      Notes: r.notes || ''
    }));
    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.json_to_sheet(rows2);
    XLSX.utils.book_append_sheet(wb, ws, 'Report');
    XLSX.writeFile(wb, `Report-${(userId || 'me')}-${from}_to_${to}.xlsx`);
  }
  async function exportPDF() {
    const doc = new jsPDF('p', 'pt');
    const margin = 36;
    doc.setFontSize(16);
    doc.text('Attendance Report', margin, 30);
    doc.setFontSize(11);
    doc.text(`Range: ${from} → ${to}`, margin, 46);

    const body = rows.map(r => [
      r.date,
      r.clockIn ? new Date(r.clockIn).toLocaleString() : '-',
      r.clockOut ? new Date(r.clockOut).toLocaleString() : '-',
      fmtMin(r.regularMinutes || 0),
      fmtMin(r.overtimeMinutes || 0),
      fmtMin(r.totalMinutes || 0),
      r.notes || ''
    ]);
    doc.autoTable({
      startY: 64,
      head: [['Date','Clock In','Clock Out','Regular','Overtime','Total','Notes']],
      body,
      styles: { fontSize: 10 },
      headStyles: { fillColor: [20,20,20], textColor: [255,255,255] },
      margin: { left: margin, right: margin }
    });
    doc.save(`Report-${(userId || 'me')}-${from}_to_${to}.pdf`);
  }

  /* ===== Presets ===== */
  function setPreset(kind) {
    const d = new Date();
    const toISO = (x) => {
      const off = x.getTimezoneOffset();
      return new Date(x.getTime() - off * 60000).toISOString().slice(0, 10);
    };
    if (kind === 'today') { const t = toISO(new Date()); setFrom(t); setTo(t); }
    else if (kind === 'yesterday') { const y = new Date(d); y.setDate(d.getDate() - 1); const t = toISO(y); setFrom(t); setTo(t); }
    else if (kind === 'week') {
      const day = d.getDay(); const monday = new Date(d); const sunday = new Date(d);
      monday.setDate(d.getDate() - ((day + 6) % 7)); sunday.setDate(monday.getDate() + 6);
      setFrom(toISO(monday)); setTo(toISO(sunday));
    } else if (kind === 'month') {
      const first = new Date(d.getFullYear(), d.getMonth(), 1);
      const last = new Date(d.getFullYear(), d.getMonth() + 1, 0);
      setFrom(toISO(first)); setTo(toISO(last));
    }
  }

  function logout() {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('auth');
    window.location.href = '/login';
  }

  /* ===== Render ===== */
  const totals = useMemo(() => {
    const sum = (k) => rows.reduce((a, r) => a + (Number(r[k]) || 0), 0);
    return { reg: sum('regularMinutes'), ot: sum('overtimeMinutes'), all: sum('totalMinutes') };
  }, [rows]);

  return (
    <div className="page ltr">
      <Toast {...(msg || {})} onClose={() => setMsg(null)} show={!!msg} />

      {/* Edit modal */}
      {editing && (
        <Modal title={`Edit ${toLocalDate(editing.date || editing.clockIn)}`} onClose={() => setEditing(null)}>
          <div className="stack">
            <label className="stack">
              <span>Clock In</span>
              <input className="input" type="datetime-local" value={editClockIn} onChange={e=>setEditClockIn(e.target.value)} />
            </label>
            <label className="stack">
              <span>Clock Out</span>
              <input className="input" type="datetime-local" value={editClockOut} onChange={e=>setEditClockOut(e.target.value)} />
            </label>
            <label className="stack">
              <span>Notes</span>
              <textarea className="input" rows={3} value={editNotes} onChange={e=>setEditNotes(e.target.value)} />
            </label>
            <div className="row end gap">
              <button className="btn" onClick={saveEdit}>Save</button>
              <button className="btn gray" onClick={() => setEditing(null)}>Cancel</button>
            </div>
          </div>
        </Modal>
      )}

      {/* Top bar */}
      <div className="topbar">
        <div className="row gap">
          <button className="chip" onClick={() => setDark(v => !v)}>{dark ? 'Light mode' : 'Dark mode'}</button>
          <button className="chip" onClick={() => window.open('/kiosk', '_blank')}>QR Kiosk</button>
          {me?.role === 'admin' && <button className="chip" onClick={() => (window.location.href = '/admin/users')}>User Management</button>}
        </div>
        <div className="row gap">
          <div className="clock">{new Date().toLocaleTimeString()}</div>
          <div className="userbox">
            <img src="/logo.png" alt="Logo" />
            <div>
              <div className="name">Hello {me?.name || '...'}</div>
              <div className="email">{me?.email || ''}</div>
            </div>
          </div>
        </div>
      </div>

      {/* Layout */}
      <div className="grid">
        {/* Today */}
        <section className="card">
          <div className="card-head">
            <h2>Today</h2>
            <div className="muted">{today ? today.date : todayISO()}</div>
          </div>

          <div className="row gap wrap end mb">
            <button className="btn" disabled={shiftOpen} onClick={doClockIn}>Clock In</button>
            <button className="btn gray" disabled={!shiftOpen || onBreak} onClick={startBreak}>Start Break</button>
            <button className="btn gray" disabled={!onBreak} onClick={endBreak}>End Break</button>
            <button className="btn gray" disabled={!shiftOpen} onClick={doClockOut}>Clock Out</button>
          </div>

          {loadingToday ? (
            <div className="skeleton big" />
          ) : !today ? (
            <div className="muted">No active shift</div>
          ) : (
            <div className="two">
              <div className="stack">
                <div><b>Clock In:</b> {toLocalTime(today.clockIn)}</div>
                <div><b>Clock Out:</b> {toLocalTime(today.clockOut)}</div>
                <div><b>Live total:</b> {fmtMin(liveMinutes)}</div>
                <div><b>Regular:</b> {fmtMin(today.regularMinutes || 0)}</div>
                <div><b>Overtime:</b> {fmtMin(today.overtimeMinutes || 0)}</div>
              </div>
              <div className="stack">
                <div className="b">Breaks:</div>
                <ul className="bullets">
                  {(today.breaks || []).map((b, i) => (
                    <li key={i}>
                      {toLocalTime(b.start)} → {b.end ? toLocalTime(b.end) : '—'}
                    </li>
                  ))}
                  {!today.breaks?.length && <li>—</li>}
                </ul>

                <div className="stack" style={{marginTop:8}}>
                  <label className="b">Notes:</label>
                  <textarea className="input" rows={3} value={noteDraft} onChange={e=>setNoteDraft(e.target.value)} placeholder="Add a note..." />
                  <div className="row end">
                    <button className="btn" disabled={!today?._id} onClick={saveTodayNote}>Save note</button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </section>

        {/* Report */}
        <section className="card">
          <div className="card-head">
            <h2>Report by dates</h2>
            <div className="row gap">
              <button className="link-btn" onClick={exportCSV}>CSV</button>
              <button className="link-btn" onClick={exportExcel}>Excel</button>
              <button className="link-btn" onClick={exportPDF}>PDF</button>
            </div>
          </div>

          <div className="filters">
            <div className="row gap wrap">
              <label>From</label>
              <input className="input" type="date" value={from} onChange={e => setFrom(e.target.value)} />
              <label>To</label>
              <input className="input" type="date" value={to} onChange={e => setTo(e.target.value)} />
              <select className="input" value={userId} onChange={e => setUserId(e.target.value)}>
                <option value="">— only me —</option>
              </select>
              <div className="row gap">
                <button className="chip" onClick={() => setPreset('today')}>Today</button>
                <button className="chip" onClick={() => setPreset('yesterday')}>Yesterday</button>
                <button className="chip" onClick={() => setPreset('week')}>This week</button>
                <button className="chip" onClick={() => setPreset('month')}>This month</button>
              </div>
            </div>
          </div>

          <div className="table-wrap">
            <table className="table">
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Clock In</th>
                  <th>Clock Out</th>
                  <th>Regular</th>
                  <th>Overtime</th>
                  <th>Total</th>
                  <th>Notes</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {loadingReport ? (
                  Array.from({ length: 6 }).map((_, i) => (
                    <tr key={i}><td colSpan="8"><div className="skeleton"/></td></tr>
                  ))
                ) : rows.map((r) => (
                  <tr key={r._id || r.date}>
                    <td>{r.date}</td>
                    <td>{toLocalTime(r.clockIn)}</td>
                    <td>{toLocalTime(r.clockOut)}</td>
                    <td>{fmtMin(r.regularMinutes || 0)}</td>
                    <td>{fmtMin(r.overtimeMinutes || 0)}</td>
                    <td>{fmtMin(r.totalMinutes || 0)}</td>
                    <td>{r.notes || ''}</td>
                    <td>
                      <button className="btn tiny" onClick={() => openEdit(r)}>Edit</button>
                    </td>
                  </tr>
                ))}
              </tbody>
              {!loadingReport && (
                <tfoot>
                  <tr>
                    <td>Totals</td>
                    <td></td><td></td>
                    <td>{fmtMin(totals.reg)}</td>
                    <td>{fmtMin(totals.ot)}</td>
                    <td>{fmtMin(totals.all)}</td>
                    <td></td><td></td>
                  </tr>
                </tfoot>
              )}
            </table>
          </div>
        </section>
      </div>

      <div className="footer">
        <button className="btn gray" onClick={logout}>Logout</button>
        <div className="muted">© {new Date().getFullYear()}</div>
      </div>

      {/* Styles (LTR + Dark compatible) */}
      <style>{`
        :root { --bg:#f6f7fb; --fg:#0f172a; --muted:#6b7280; --card:#fff; --border:#e5e7eb; --chip:#eef2f7; --chip-fg:#0f172a; }
        html.dark { --bg:#0f172a; --fg:#e5e7eb; --muted:#93a1b5; --card:#111827; --border:#273043; --chip:#1f2937; --chip-fg:#e5e7eb; }
        body { background:var(--bg); color:var(--fg); }
        .ltr { direction:ltr; }
        .page { min-height:100vh; display:flex; flex-direction:column; gap:16px; }
        .topbar { display:flex; justify-content:space-between; align-items:center; padding:14px 16px; }
        .row { display:flex; align-items:center; }
        .row.end { justify-content:flex-end; }
        .row.wrap { flex-wrap:wrap; }
        .gap { gap:10px; }
        .mb { margin-bottom:10px; }
        .clock { background:#000; color:#fff; border-radius:999px; padding:4px 10px; font-size:12px; }
        .userbox { display:flex; gap:10px; align-items:center; }
        .userbox img { width:32px; height:32px; border-radius:6px; }
        .userbox .name { font-weight:700; font-size:14px; }
        .userbox .email { font-size:12px; color:var(--muted); }
        .chip { background:var(--chip); color:var(--chip-fg); border:1px solid var(--border); border-radius:10px; padding:6px 10px; font-size:13px; }
        .grid { display:grid; grid-template-columns:1fr 1fr; gap:16px; padding:0 16px 20px; }
        @media (max-width: 1024px){ .grid{ grid-template-columns:1fr; } }
        .card { background:var(--card); border:1px solid var(--border); border-radius:16px; box-shadow:0 1px 2px rgba(0,0,0,.03); padding:14px; }
        .card-head { display:flex; justify-content:space-between; align-items:center; margin-bottom:8px; }
        .muted { color:var(--muted); font-size:13px; }
        .filters { padding:8px 0; border-top:1px solid var(--border); border-bottom:1px solid var(--border); margin-bottom:10px; }
        .input { border:1px solid var(--border); background:transparent; color:var(--fg); border-radius:10px; padding:6px 10px; font-size:14px; }
        .btn { background:#111; color:#fff; border:none; border-radius:10px; padding:8px 14px; font-weight:600; }
        .btn.gray { background:var(--chip); color:var(--chip-fg); border:1px solid var(--border); }
        .btn.tiny { padding:4px 8px; font-size:12px; }
        .link-btn { background:none; border:none; text-decoration:underline; color:var(--fg); cursor:pointer; padding:0; }
        .table-wrap { max-height:480px; overflow:auto; border:1px solid var(--border); border-radius:10px; }
        .table { width:100%; border-collapse:separate; border-spacing:0; }
        .table thead th { position:sticky; top:0; background:var(--chip); color:var(--chip-fg); text-align:left; padding:10px; border-bottom:1px solid var(--border); font-weight:700; }
        .table tbody td { padding:10px; border-bottom:1px solid var(--border); }
        .table tbody tr:nth-child(even){ background:rgba(0,0,0,.02); }
        html.dark .table tbody tr:nth-child(even){ background:rgba(255,255,255,.04); }
        .table tfoot td { padding:10px; background:var(--chip); color:var(--chip-fg); font-weight:700; position:sticky; bottom:0; text-align:left; }
        .skeleton { height:36px; background:linear-gradient(90deg, rgba(0,0,0,.06), rgba(0,0,0,.02), rgba(0,0,0,.06)); animation:s 1.4s infinite; border-radius:8px; }
        .skeleton.big { height:120px; }
        @keyframes s { 0%{background-position:-200px 0} 100%{background-position:200px 0} }
        .two { display:grid; grid-template-columns:1fr 1fr; gap:14px; }
        @media (max-width: 680px){ .two{ grid-template-columns:1fr; } }
        .stack { display:flex; flex-direction:column; gap:6px; }
        .b { font-weight:700; }
        .bullets { margin:0; padding-left:18px; }
        .bullets li { margin:4px 0; }
        .footer { padding:12px 16px; display:flex; justify-content:space-between; align-items:center; }
      `}</style>
    </div>
  );
}

/* ===== Hooks ===== */
function useLiveMinutes(today) {
  const [min, setMin] = useState(0);
  useEffect(() => {
    function calc() {
      if (!today?.clockIn) return setMin(0);
      const start = new Date(today.clockIn).getTime();
      const now = Date.now();
      let workingMs = now - start;
      const breaks = today.breaks || [];
      for (const b of breaks) {
        const s = b.start ? new Date(b.start).getTime() : null;
        const e = b.end ? new Date(b.end).getTime() : null;
        if (s) workingMs -= Math.max(0, (e || now) - s);
      }
      setMin(Math.max(0, Math.floor(workingMs / 60000)));
    }
    calc(); const t = setInterval(calc, 1000);
    return () => clearInterval(t);
  }, [today?.clockIn, JSON.stringify(today?.breaks || [])]);
  return min;
}
