// client/src/pages/AdminUsers.js
import React, { useEffect, useState } from 'react';
import { api } from '../api';
import { useAuth } from '../context/AuthContext';
import Toast from '../components/Toast';

export default function AdminUsers() {
  const { auth } = useAuth();
  const [rows, setRows] = useState([]);
  const [msg, setMsg] = useState(null);
  const [loading, setLoading] = useState(false);

  const isAdmin = auth?.user?.role === 'admin';

  useEffect(() => {
    if (!isAdmin) return;
    const load = async () => {
      setLoading(true);
      try {
        const { data } = await api.get('/admin/users');
        setRows(data || []);
      } catch (err) {
        const status = err?.response?.status;
        if (status === 401) setMsg({ type: 'error', text: 'נדרש להתחבר מחדש' });
        else if (status === 403) setMsg({ type: 'error', text: 'אין הרשאה (Admin only)' });
        else setMsg({ type: 'error', text: 'שגיאה בטעינת משתמשים' });
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [isAdmin]);

  if (!auth?.user) return null;
  if (!isAdmin) return <div style={{ color: '#d00', padding: 20, textAlign: 'end' }}>Admin only</div>;

  return (
    <div className="container" style={{ padding: 20 }}>
      <h2 style={{ textAlign: 'end' }}>ניהול משתמשים</h2>
      {msg && <Toast type={msg.type} message={msg.text} onClose={() => setMsg(null)} />}
      {loading ? (
        <div style={{ padding: 20, textAlign: 'center' }}>טוען…</div>
      ) : (
        <div style={{ overflowX: 'auto' }}>
          <table className="table">
            <thead>
              <tr>
                <th>שם</th>
                <th>אימייל</th>
                <th>תפקיד</th>
                <th>מחלקה</th>
                <th>פעיל</th>
              </tr>
            </thead>
            <tbody>
              {rows.map((u) => (
                <tr key={u._id || u.id}>
                  <td>{u.name}</td>
                  <td>{u.email}</td>
                  <td>{u.role}</td>
                  <td>{u.department || '-'}</td>
                  <td>{u.active ? 'כן' : 'לא'}</td>
                </tr>
              ))}
              {!rows.length && (
                <tr>
                  <td colSpan={5} style={{ textAlign: 'center' }}>אין נתונים</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
