import React, { useEffect, useState } from 'react';
import { api } from '../api';
import Toast from '../components/Toast';

export default function AdminUsers() {
  const [me, setMe] = useState(null);
  const [rows, setRows] = useState([]);
  const [err, setErr] = useState('');
  const [toast, setToast] = useState({ show:false, type:'success', title:'', message:'' });

  const [form, setForm] = useState({ name:'', email:'', password:'', role:'user' });
  const [busy, setBusy] = useState(false);

  const showToast = (type, title, message='') => setToast({ show:true, type, title, message });
  const closeToast = () => setToast(t => ({ ...t, show:false }));

  useEffect(() => {
    (async () => {
      const m = await api('/api/auth/me');
      setMe(m.user);
      if (m.user.role !== 'admin') {
        setErr('Admin only');
        return;
      }
      await load();
    })().catch(e => setErr(e.message));
  }, []);

  async function load() {
    const data = await api('/api/admin/users');
    setRows(data);
  }

  async function createUser(e) {
    e.preventDefault();
    setBusy(true);
    try {
      await api('/api/admin/users', { method:'POST', body: JSON.stringify(form) });
      setForm({ name:'', email:'', password:'', role:'user' });
      await load();
      showToast('success','משתמש נוצר בהצלחה');
    } catch (e) {
      showToast('error', e.message);
    } finally {
      setBusy(false);
    }
  }

  async function toggleActive(u) {
    await api(`/api/admin/users/${u._id}`, {
      method:'PUT',
      body: JSON.stringify({ active: !u.active })
    });
    await load();
  }

  async function resetPass(u) {
    const p = prompt('סיסמה חדשה למשתמש:');
    if (!p) return;
    await api(`/api/admin/users/${u._id}`, {
      method:'PUT',
      body: JSON.stringify({ resetPassword: p })
    });
    showToast('success','הסיסמה אופסה');
  }

  return (
    <div className="min-h-screen p-4">
      <Toast show={toast.show} type={toast.type} title={toast.title} message={toast.message} onClose={closeToast}/>
      <header className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <img src="/logo.png" alt="Costoro" className="w-10 h-10 rounded-md" />
          <div>
            <div className="font-semibold text-lg">ניהול משתמשים</div>
            <div className="text-sm text-slate-500">שלום {me?.name}</div>
          </div>
        </div>
        <a className="px-4 py-2 rounded-xl border bg-white" href="/">חזרה לדשבורד</a>
      </header>

      {err && <div className="text-rose-600 mb-4">{err}</div>}

      {!err && (
        <div className="grid lg:grid-cols-2 gap-6">
          <div className="bg-white rounded-2xl shadow p-5">
            <h2 className="text-lg font-semibold mb-3">יצירת משתמש</h2>
            <form onSubmit={createUser} className="grid gap-3">
              <input className="border rounded-xl px-3 py-2" placeholder="שם" value={form.name} onChange={e=>setForm(v=>({...v,name:e.target.value}))} required />
              <input className="border rounded-xl px-3 py-2" placeholder="אימייל" type="email" value={form.email} onChange={e=>setForm(v=>({...v,email:e.target.value}))} required />
              <input className="border rounded-xl px-3 py-2" placeholder="סיסמה" type="text" value={form.password} onChange={e=>setForm(v=>({...v,password:e.target.value}))} required />
              <select className="border rounded-xl px-3 py-2" value={form.role} onChange={e=>setForm(v=>({...v,role:e.target.value}))}>
                <option value="user">User</option>
                <option value="admin">Admin</option>
              </select>
              <button className="btn" disabled={busy}>צור משתמש</button>
            </form>
          </div>

          <div className="bg-white rounded-2xl shadow p-5 overflow-auto">
            <h2 className="text-lg font-semibold mb-3">כל המשתמשים</h2>
            <table className="min-w-full text-sm">
              <thead>
                <tr className="text-left border-b">
                  <th className="py-2">שם</th>
                  <th className="py-2">אימייל</th>
                  <th className="py-2">תפקיד</th>
                  <th className="py-2">סטטוס</th>
                  <th className="py-2"></th>
                </tr>
              </thead>
              <tbody>
                {rows.map(u=>(
                  <tr key={u._id} className="border-b">
                    <td className="py-2">{u.name}</td>
                    <td className="py-2">{u.email}</td>
                    <td className="py-2">{u.role}</td>
                    <td className="py-2">
                      <span className={`px-2 py-1 rounded text-white text-xs ${u.active?'bg-emerald-600':'bg-slate-400'}`}>
                        {u.active ? 'פעיל' : 'מושבת'}
                      </span>
                    </td>
                    <td className="py-2 flex gap-2">
                      <button className="px-3 py-1 rounded-xl border" onClick={()=>toggleActive(u)}>{u.active?'השבת':'הפעל'}</button>
                      <button className="px-3 py-1 rounded-xl border" onClick={()=>resetPass(u)}>איפוס סיסמה</button>
                    </td>
                  </tr>
                ))}
                {!rows.length && <tr><td className="py-4 text-slate-400" colSpan={5}>אין משתמשים</td></tr>}
              </tbody>
            </table>
          </div>
        </div>
      )}

      <style>{`.btn{background:black;color:white;border-radius:0.75rem;padding:0.6rem 1rem}`}</style>
    </div>
  );
}
