// client/src/App.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import AdminUsers from './pages/AdminUsers';
import Kiosk from './pages/Kiosk';
import QRScan from './pages/QRScan';

const getAuth = () => {
  const token = localStorage.getItem('token');
  if (token) return { token };
  try {
    const auth = JSON.parse(localStorage.getItem('auth') || '{}');
    if (auth?.token) return auth;
  } catch {}
  return null;
};
const isAuthed = () => !!getAuth()?.token;
const isAdmin = () => getAuth()?.user?.role === 'admin';

export default function App() {
  return (
    <Routes>
      <Route path="/login" element={<Login />} />
      <Route path="/register" element={<Register />} />

      <Route path="/" element={isAuthed() ? <Dashboard /> : <Navigate to="/login" replace />} />
      <Route path="/admin/users" element={isAuthed() ? (isAdmin() ? <AdminUsers /> : <div style={{ padding: 20, color: 'red', textAlign: 'end' }}>אין לך הרשאה לגשת לעמוד זה</div>) : <Navigate to="/login" replace />} />

      {/* Kiosk (למסך בלובי) – רק למנהלים */}
      <Route path="/kiosk" element={isAuthed() ? (isAdmin() ? <Kiosk /> : <div style={{ padding: 20, color: 'red', textAlign: 'end' }}>אין לך הרשאה לגשת לעמוד זה</div>) : <Navigate to="/login" replace />} />

      {/* דף סריקה בטלפון – דורש התחברות בטלפון */}
      <Route path="/qr" element={isAuthed() ? <QRScan /> : <Navigate to="/login" replace />} />

      <Route path="*" element={<Navigate to={isAuthed() ? '/' : '/login'} replace />} />
    </Routes>
  );
}
