import React, { useState } from 'react';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [err, setErr] = useState('');

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000';

  async function handleLogin(e) {
    e.preventDefault();
    setErr('');
    try {
      const res = await fetch(`${API_URL}/api/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      const json = await res.json();
      if (!res.ok) throw new Error(json.message || 'Login failed');
      localStorage.setItem('token', json.token);
      localStorage.setItem('user', JSON.stringify(json.user));
      window.location.href = '/';
    } catch (e) {
      setErr(e.message);
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center p-4">
      <div className="w-full max-w-md bg-white rounded-2xl shadow-lg p-8">
        <div className="flex items-center gap-3 mb-6">
          <img src="/logo.png" alt="Costoro" className="w-10 h-10" />
          <h1 className="text-2xl font-semibold">Costoro Timewatch</h1>
        </div>

        <form onSubmit={handleLogin} className="space-y-4">
          <div>
            <label className="block text-sm mb-1">אימייל</label>
            <input
              className="w-full border rounded-xl px-3 py-2 focus:outline-none focus:ring"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="you@example.com"
              required
            />
          </div>
          <div>
            <label className="block text-sm mb-1">סיסמה</label>
            <input
              className="w-full border rounded-xl px-3 py-2 focus:outline-none focus:ring"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="••••••••"
              required
            />
          </div>
          {err && <div className="text-red-600 text-sm">{err}</div>}
          <button
            className="w-full bg-black text-white rounded-xl py-2.5 font-medium hover:opacity-90"
            type="submit"
          >
            התחברות
          </button>
        </form>
      </div>
    </div>
  );
}
