// client/src/App.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import AdminUsers from './pages/AdminUsers';

// פונקציה לבדוק אם יש טוקן כלשהו
const getAuth = () => {
  const token = localStorage.getItem('token');
  if (token) return { token };
  try {
    const auth = JSON.parse(localStorage.getItem('auth') || '{}');
    if (auth?.token) return auth;
  } catch {}
  return null;
};

const isAuthed = () => !!getAuth()?.token;

// Guard לאדמין בלבד
const isAdmin = () => {
  const auth = getAuth();
  return auth?.user?.role === 'admin';
};

export default function App() {
  return (
    <Routes>
      <Route path="/login" element={<Login />} />
      <Route
        path="/"
        element={isAuthed() ? <Dashboard /> : <Navigate to="/login" replace />}
      />
      <Route
        path="/admin/users"
        element={
          isAuthed()
            ? isAdmin()
              ? <AdminUsers />
              : <div style={{ padding: 20, color: 'red' }}>אין לך הרשאה לגשת לעמוד זה</div>
            : <Navigate to="/login" replace />
        }
      />
      <Route
        path="*"
        element={<Navigate to={isAuthed() ? '/' : '/login'} replace />}
      />
    </Routes>
  );
}
